<?php

$infile = '../../assets/AMillionRandomDigits.bin';

define("DEBUG", false);

function _echo($string) {
	if (DEBUG) echo $string;
}

// Sample data from the dobbs random bits file

//$originalData =  "";

// Sample data from an ascii file (lorem ipsum)

$originalData = "";

//$originalData = substr($originalData, 0,64);

$originalData .= "1"; // This stops infinite loops for some reason - sequences ending in zero repeat infinitely - adding this also gives us certainty that the last character will always be zero

$predictedRepeatLength = pow(2,ceil(log(strlen($originalData),2)));

// Make a copy of the original data for processing each step
$stepData = $originalData;

// Count the number of ones in the data (useful for statistics)
$minOnesCount = 0;
$maxOnesCount = 0;
$minOnesCountStep = 0;
$maxOnesCountStep = 0;
$minOnesStepData = $originalData;
$maxOnesStepData = $originalData;

// Count the number of steps processed
$currentStep = 0;

// Track if the sequence has repeated yet
$repeatLength = 0;
$repeated = false;
$startOnesCount = substr_count($originalData, "1");

// Track variation in steps
$minimumVariation = 0;
$minimumVariationStep = 0;
$minimumVariationStepData = $originalData;
$originalVariation = 0;
for($i = 0; $i < strlen($stepData); $i++) {
	// What we are essentially checking here is if the next bit in the previous step is different to the bit before it, or in other words variations in direction from 1 to 0.
	// It behaves like a celular autonoma and can be reversed. We are seeking data with minimal variation.
	if (!($i+1 < strlen($originalData)) || $originalData[$i] != $originalData[$i+1]) {
		$originalVariation += 1;
	}
}

// Some output for visual analysis
_echo("$originalData [$startOnesCount]\n");

while (!$repeated) {
	$progress = intval($currentStep / $predictedRepeatLength * 100);
	if (!DEBUG) echo "\033[2K\rWorking ($progress)%";
	$repeatLength ++;
	$variation = 0;
	for($i = 0; $i < strlen($stepData); $i++) {
		// What we are essentially checking here is if the next bit in the previous step is different to the bit before it, or in other words variations in direction from 1 to 0.
		// It behaves like a celular autonoma and can be reversed. We are seeking data with minimal variation.
		if (!($i+1 < strlen($stepData)) || $stepData[$i] != $stepData[$i+1]) {
			$stepData[$i] = "1";
			$variation += 1;
		} else {
			$stepData[$i] = "0";
		}
	}
	// Found a step with lower variation (fewer changes between 0 and 1)
	if ($currentStep == 0 || $variation < $minimumVariation) {
		$minimumVariation = $variation;
		$minimumVariationStep = $currentStep;
		$minimumVariationStepData = $stepData;		
	}
	// Count the ones on the row
	$onesCount = substr_count($stepData, "1");
	if ($currentStep == 0 || $onesCount < $minOnesCount) {
		$minOnesCount = $onesCount;
		$minOnesCountStep = $currentStep;
		$minOnesStepData = $stepData;
	}
	if ($currentStep == 0 || $onesCount > $maxOnesCount) {
		$maxOnesCount = $onesCount;
		$maxOnesCountStep = $currentStep;
		$maxOnesStepData = $stepData;
	}
	_echo("$stepData");
	_echo(" [ones: $onesCount] [variation: $variation]");
	$onesCount = 0;
	if ($stepData == $originalData) {
		_echo(" (repeat @$repeatLength)");
		$repeated = true;		
		if ($minimumVariationStep == $currentStep) $minimumVariationStep = 0; // This means the original data had the least variation to begin with.
		if (!DEBUG) echo "\033[2K\rDone.\n";
	}	
	_echo("\n");
	$currentStep ++;
}

// Display information on difference in ones

echo "\nDifference in count of ones:\n";
$minRange = $startOnesCount - $minOnesCount;
$maxRange = $maxOnesCount - $startOnesCount;
echo "Start ones count: $startOnesCount\n";
echo "Minimum ones: $minOnesCount (step $minOnesCountStep) [difference=$minRange]\n";
echo "Maximum ones: $maxOnesCount (step $maxOnesCountStep) [difference=$maxRange]\n";
$zerosOrOnes = ($minRange > $maxRange) ? "zeroes" : "ones";
$bestDifferenceStepData = $zerosOrOnes == "zeroes" ? $minOnesStepData : $maxOnesStepData;
$percentageDifference = $zerosOrOnes == "zeroes" ? $minRange / strlen($originalData) : $maxRange / strlen($originalData);
echo "$bestDifferenceStepData\n";
echo "$percentageDifference% change in $zerosOrOnes count\n";
$bestDifferenceStepDataBinary = "";
for($i = 0; $i < strlen($bestDifferenceStepData); $i += 8) {
	$byte = substr($bestDifferenceStepData, $i, 8);
	$bestDifferenceStepDataBinary .= chr(bindec($byte));
}
file_put_contents("difference.bin", $bestDifferenceStepDataBinary);

// Display information on difference in variation

echo "\nDifference in variation (changes between 0 and 1):\n";
echo "$minimumVariationStepData\n";
echo "Original variation: $originalVariation\n";
echo "Minimum variation: $minimumVariation (step $minimumVariationStep)\n";
$variationDifference = $originalVariation - $minimumVariation;
$variationPercentage = 1 - ($minimumVariation / $originalVariation);
echo "Variation difference: $variationDifference ($variationPercentage%)\n";
$minimumVariationStepDataBinary = "";
for($i = 0; $i < strlen($minimumVariationStepData); $i += 8) {
	$byte = substr($minimumVariationStepData, $i, 8);
	$minimumVariationStepDataBinary .= chr(bindec($byte));
}
file_put_contents("variation.bin", $minimumVariationStepDataBinary);